let mybutton=document.getElementById("myBtn");function scrollFunction(){document.body.scrollTop>60||document.documentElement.scrollTop>60?(document.getElementById("shrinkTitle").style.fontSize="4vw",document.getElementById("hiddenTitle").style.display="none",mybutton.style.display="block"):(document.getElementById("shrinkTitle").style.fontSize="12.5vw",document.getElementById("hiddenTitle").style.display="block",mybutton.style.display="none")}function topFunction(){document.body.scrollTop=0,document.documentElement.scrollTop=0}window.onscroll=function(){scrollFunction()};